import React from 'react'

import { NavLink } from 'react-router-dom'

import styled, { keyframes } from 'styled-components'

interface MobileMenuProps {
  onDismiss: () => void
  visible?: boolean
}

const MobileMenu: React.FC<MobileMenuProps> = ({ onDismiss, visible }) => {
  if (visible) {
    document.body.style.overflow='hidden';
    return (
      <StyledMobileMenuWrapper>
        <StyledBackdrop onClick={onDismiss} />
        <StyledMobileMenu>
          <StyledLink
            exact
            activeClassName='active'
            to='/wlkr'
            onClick={onDismiss}
          >
            DeFi Pulse Index
          </StyledLink>
          <StyledLink
            exact
            activeClassName='active'
            to='/mvi'
            onClick={onDismiss}
          >
            Metaverse Index
          </StyledLink>
          <StyledLink
            exact
            activeClassName='active'
            to='/ethfli'
            onClick={onDismiss}
          >
            ETH 2x Flexible Leverage Index
          </StyledLink>
          <StyledLink
            exact
            activeClassName='active'
            to='/btcfli'
            onClick={onDismiss}
          >
            BTC 2x Flexible Leverage Index
          </StyledLink>
          <StyledLink
            exact
            activeClassName='active'
            to='/ethflip'
            onClick={onDismiss}
          >
            ETH 2x FLI - Polygon
          </StyledLink>
          <StyledLink
            exact
            activeClassName='active'
            to='/ieth'
            onClick={onDismiss}
          >
            iETH FLI - Polygon
          </StyledLink>
          <StyledLink
            exact
            activeClassName='active'
            to='/matic2x'
            onClick={onDismiss}
          >
            MATIC 2x FLI - Polygon
          </StyledLink>
          <StyledLink
            exact
            activeClassName='active'
            to='/imatic'
            onClick={onDismiss}
          >
            iMATIC FLI - Polygon
          </StyledLink>
          <StyledLink
            exact
            activeClassName='active'
            to='/bed'
            onClick={onDismiss}
          >
            Bankless BED Index
          </StyledLink>
          <StyledLink
            exact
            activeClassName='active'
            to='/data'
            onClick={onDismiss}
          >
            Data Economy Index
          </StyledLink>
          <StyledLink
            exact
            activeClassName='active'
            to='/gmi'
            onClick={onDismiss}
          >
            Bankless GMI Index
          </StyledLink>
          <StyledLink
            exact
            activeClassName='active'
            to='/index'
            onClick={onDismiss}
          >
            Index Token
          </StyledLink>
          <StyledLink
            exact
            activeClassName='active'
            to='/liquidity-mining'
            onClick={onDismiss}
          >
            Liquidity Mining
          </StyledLink>
        </StyledMobileMenu>
      </StyledMobileMenuWrapper>
    )
  }

  else {
    document.body.style.overflow='';
  }

  return null
}

const StyledBackdrop = styled.div`
  background-color: ${(props) => props.theme.colors.black};
  opacity: 0.75;
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
`

const StyledMobileMenuWrapper = styled.div`
  display: flex;
  flex-direction: column;
  position: fixed;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: 1000;
`

const slideIn = keyframes`
  0% {
    transform: translateX(0)
  }
  100% {
    transform: translateX(-100%);
  }
`

const StyledMobileMenu = styled.div`
  animation: ${slideIn} 0.3s forwards ease-out;
  background: ${(props) => props.theme.baseBg};
  display: flex;
  flex: 1;
  flex-direction: column;
  justify-content: center;
  position: absolute;
  top: 0;
  left: 100%;
  bottom: 0;
  width: calc(100% - 48px);
  padding-top: 200px;
  padding-bottom: 100px;
  overflow-y: scroll;
`

const StyledLink = styled(NavLink)`
  box-sizing: border-box;
  color: ${(props) => props.theme.colors.grey[500]};
  font-size: 18px;
  font-weight: 700;
  padding: ${(props) => props.theme.spacing[3]}px
    ${(props) => props.theme.spacing[4]}px;
  text-align: center;
  text-decoration: none;
  width: 100%;
  &:hover {
    color: ${(props) => props.theme.colors.grey[600]};
  }
  &.active {
    color: ${(props) => props.theme.colors.primary.main};
  }
`

const StyledOutboundLink = styled.a`
  box-sizing: border-box;
  color: ${(props) => props.theme.colors.grey[500]};
  font-size: 18px;
  font-weight: 700;
  padding: ${(props) => props.theme.spacing[3]}px
    ${(props) => props.theme.spacing[4]}px;
  text-align: center;
  text-decoration: none;
  width: 100%;
  &:hover {
    color: ${(props) => props.theme.colors.grey[600]};
  }
  &.active {
    color: ${(props) => props.theme.colors.primary.main};
  }
`

export default MobileMenu
